import glob
import pprint
pp = pprint.PrettyPrinter(indent=4)

parent_path = './'
ext_srting = 'mp4'

files_list = glob.glob(f'{parent_path}/**/*.{ext_srting}', recursive = True)

# print(files_list)
# pp.pprint(files_list)

def sort_func(elem):
    final_n = ''
    s_elem = elem.split('/')
    for i in range(1, len(s_elem)):
        num_el = s_elem[i].split(' ')[0]
        final_n  = final_n + num_el
    return int(final_n)

files_list.sort(key=sort_func)

def print_one_b_one():
    for i in files_list:
        print(i)

